def ChangeButtonNamesVoice(buttonPosition, buttonText):
    global voiceDropButtonName, voiceHoldButtonName, voiceLeftButtonName, voiceRightButtonName

    if buttonPosition == 171:
        voiceRightButtonName = buttonText
        VoicePart.VoiceDetection(rightVoice=voiceRightButtonName, leftVoice=voiceLeftButtonName,
                                 holdVoice=voiceHoldButtonName, dropVoice=voiceDropButtonName)
    elif buttonPosition == 217:
        print("rightbutton")
        print(buttonText)
        voiceLeftButtonName = buttonText
        VoicePart.VoiceDetection(rightVoice=voiceRightButtonName, leftVoice=voiceLeftButtonName,
                                 holdVoice=voiceHoldButtonName, dropVoice=voiceDropButtonName)
    elif buttonPosition == 263:
        voiceHoldButtonName = buttonText
        VoicePart.VoiceDetection(rightVoice=voiceRightButtonName, leftVoice=voiceLeftButtonName,
                                 holdVoice=voiceHoldButtonName, dropVoice=voiceDropButtonName)
    elif buttonPosition == 309:
        voiceDropButtonName = buttonText
        VoicePart.VoiceDetection(rightVoice=voiceRightButtonName, leftVoice=voiceLeftButtonName,
                                 holdVoice=voiceHoldButtonName, dropVoice=voiceDropButtonName)


    button = Button(
        bg="#FFFFFF",
        text=buttonText,
        borderwidth=0,
        highlightthickness=0,
        command=lambda: [button.destroy(), VoiceChangeButton(buttonPosition)],
        relief="flat"
    )
    button.place(
        x=755,
        y=buttonPosition,
        width=98,
        height=28
    )



def VoiceChangeButton(ButtonPosition):

    print("çağrıldı")
    voiceDetectionNew = VoicePart.VoiceDetection(rightVoice=voiceRightButtonName,leftVoice=voiceLeftButtonName,
                                                 holdVoice=voiceHoldButtonName, dropVoice=voiceDropButtonName)
    command = voiceDetectionNew.DetectVoice()
    print(command)
    ChangeButtonNamesVoice(ButtonPosition, command)



